import React, { useState } from 'react';
import { supabase } from '../lib/supabase';
import { AnalyzingLoader } from './Layout';
import { FaPassport, FaFileAlt, FaMoneyCheckAlt, FaCheckCircle, FaTimesCircle } from 'react-icons/fa';

const REQUIRED_DOCS = [
  { type: 'passport', label: 'Passport (photo page)', icon: <FaPassport className="text-blue-600 h-6 w-6" />, instruction: 'Upload a clear scan or photo of your passport (photo page).' },
  { type: 'diploma', label: 'High School/College Diploma', icon: <FaFileAlt className="text-green-600 h-6 w-6" />, instruction: 'Upload your high school or college diploma/certificate.' },
  { type: 'funds_proof', label: 'Proof of Funds (bank statement)', icon: <FaMoneyCheckAlt className="text-yellow-600 h-6 w-6" />, instruction: 'Upload a recent bank statement showing at least US$ 22,000 (student) or US$ 5,000 per dependent.' },
];

export const DocumentUpload: React.FC<{ userId: string; onComplete?: () => void }> = ({ userId, onComplete }) => {
  const [files, setFiles] = useState<{ [key: string]: File | null }>({});
  const [uploading, setUploading] = useState(false);
  const [analyzing, setAnalyzing] = useState(false);
  const [status, setStatus] = useState<'idle' | 'uploaded' | 'analyzing' | 'approved'>('idle');
  const [error, setError] = useState<string | null>(null);
  const [fileStatus, setFileStatus] = useState<{ [key: string]: 'idle' | 'selected' | 'error' }>({});

  const handleFileChange = (type: string, file: File | null) => {
    setFiles(prev => ({ ...prev, [type]: file }));
    setFileStatus(prev => ({ ...prev, [type]: file ? 'selected' : 'idle' }));
  };

  const handleUpload = async () => {
    setUploading(true);
    setError(null);
    try {
      for (const doc of REQUIRED_DOCS) {
        const file = files[doc.type];
        if (!file) {
          setFileStatus(prev => ({ ...prev, [doc.type]: 'error' }));
          throw new Error(`Required file: ${doc.label}`);
        }
        const { data, error } = await supabase.storage.from('student-documents').upload(`${userId}/${doc.type}-${Date.now()}`, file, { upsert: true });
        if (error) throw error;
        await supabase.from('student_documents').insert({
          user_id: userId,
          type: doc.type,
          file_url: data?.path,
          status: 'pending',
        });
      }
      setStatus('uploaded');
      setAnalyzing(true);
      setTimeout(async () => {
        await supabase.from('student_documents').update({ status: 'approved', approved_at: new Date().toISOString() }).eq('user_id', userId);
        await supabase.from('user_profiles').update({ documents_status: 'approved', documents_uploaded: true }).eq('user_id', userId);
        setAnalyzing(false);
        setStatus('approved');
        if (onComplete) onComplete();
      }, 60000);
    } catch (err: any) {
      setError(err.message || 'Upload error');
    } finally {
      setUploading(false);
    }
  };

  if (analyzing) return <AnalyzingLoader message="Analyzing your documents..." />;
  if (status === 'approved') return (
    <div className="p-8 bg-green-50 rounded-2xl text-green-700 font-bold text-center flex flex-col items-center gap-4 shadow-lg border border-green-100">
      <FaCheckCircle className="h-12 w-12 text-green-500 mb-2" />
      Documents approved! You can proceed to the next step.
    </div>
  );

  return (
    <div className="bg-white rounded-3xl shadow-2xl p-10 max-w-2xl mx-auto border border-slate-100 animate-fade-in">
      <h2 className="text-3xl font-extrabold mb-8 text-slate-800 text-center">Required Documents Upload</h2>
      <div className="grid grid-cols-1 gap-6 mb-8">
        {REQUIRED_DOCS.map(doc => (
          <div key={doc.type} className="flex flex-col md:flex-row items-center gap-4 bg-slate-50 rounded-xl p-6 border border-slate-200">
            <div className="flex-shrink-0">{doc.icon}</div>
            <div className="flex-1 w-full">
              <div className="font-bold text-slate-800 mb-1">{doc.label}</div>
              <div className="text-slate-500 text-sm mb-2">{doc.instruction}</div>
              <input
                type="file"
                accept="application/pdf,image/*"
                onChange={e => handleFileChange(doc.type, e.target.files?.[0] || null)}
                className="block w-full text-sm text-slate-700 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
              />
              {fileStatus[doc.type] === 'selected' && <div className="flex items-center gap-1 text-green-600 mt-1 text-xs"><FaCheckCircle className="h-4 w-4" /> File selected</div>}
              {fileStatus[doc.type] === 'error' && <div className="flex items-center gap-1 text-red-600 mt-1 text-xs"><FaTimesCircle className="h-4 w-4" /> Required</div>}
            </div>
          </div>
        ))}
      </div>
      {error && <div className="text-red-600 mb-4 text-center font-medium flex items-center justify-center gap-2"><FaTimesCircle className="h-5 w-5" /> {error}</div>}
      <button
        onClick={handleUpload}
        disabled={uploading || Object.values(files).some(f => !f)}
        className="w-full bg-blue-600 text-white py-4 rounded-xl font-extrabold text-lg hover:bg-blue-700 transition-all disabled:opacity-50 flex items-center justify-center gap-2 shadow-lg"
      >
        {uploading ? (
          <>
            <span className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></span>
            Uploading...
          </>
        ) : (
          <>Upload Documents</>
        )}
      </button>
    </div>
  );
};

import React, { useState } from 'react';
import { supabase } from '../lib/supabase';
import { FaPassport, FaFileAlt, FaMoneyCheckAlt, FaCheckCircle, FaTimes } from 'react-icons/fa';

interface DocumentUploadModalProps {
  userId: string;
  onSuccess: () => void;
  onClose: () => void;
}

const REQUIRED_DOCS = [
  { type: 'passport', label: 'Passport (photo page)', icon: <FaPassport className="text-blue-600 h-6 w-6" />, instruction: 'Upload a clear scan or photo of your passport (photo page).' },
  { type: 'diploma', label: 'High School/College Diploma', icon: <FaFileAlt className="text-green-600 h-6 w-6" />, instruction: 'Upload your diploma or graduation certificate.' },
  { type: 'funds', label: 'Proof of Funds', icon: <FaMoneyCheckAlt className="text-yellow-600 h-6 w-6" />, instruction: 'Upload a bank statement or proof of sufficient funds.' },
];

const DocumentUploadModal: React.FC<DocumentUploadModalProps> = ({ userId, onSuccess, onClose }) => {
  const [files, setFiles] = useState<{ [key: string]: File | null }>({ passport: null, diploma: null, funds: null });
  const [uploading, setUploading] = useState(false);
  const [analyzing, setAnalyzing] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleFileChange = (type: string, file: File | null) => {
    setFiles((prev) => ({ ...prev, [type]: file }));
  };

  const canUpload = Object.values(files).every((file) => file);

  const handleUpload = async () => {
    setUploading(true);
    try {
      for (const doc of REQUIRED_DOCS) {
        const file = files[doc.type];
        if (file) {
          const { error } = await supabase.storage.from('student-documents').upload(`${userId}/${doc.type}`, file, { upsert: true });
          if (error) throw error;
        }
      }
      setUploading(false);
      setAnalyzing(true);
      setTimeout(() => {
        setAnalyzing(false);
        setSuccess(true);
      }, 60000); // 1 minuto
    } catch (err) {
      setUploading(false);
      alert('Error uploading documents. Please try again.');
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-60">
      <div className="bg-white rounded-2xl shadow-2xl p-10 max-w-lg w-full relative animate-fade-in">
        {!analyzing && !success && (
          <>
            <button
              className="absolute top-4 right-4 text-slate-400 hover:text-slate-700 text-xl focus:outline-none"
              aria-label="Close"
              onClick={onClose}
            >
              <FaTimes />
            </button>
            <h2 className="text-2xl font-bold text-slate-900 mb-6 text-center">Upload Required Documents</h2>
            <p className="text-slate-600 text-center mb-6">Please upload all required documents to proceed. All files must be clear and legible.</p>
            <div className="space-y-5 mb-8">
              {REQUIRED_DOCS.map((doc) => (
                <div key={doc.type} className="flex items-center gap-4 bg-slate-50 rounded-xl p-4 border border-slate-200">
                  {doc.icon}
                  <div className="flex-1">
                    <div className="font-semibold text-slate-800">{doc.label}</div>
                    <div className="text-slate-500 text-xs mb-1">{doc.instruction}</div>
                    <input
                      type="file"
                      accept="image/*,application/pdf"
                      onChange={(e) => handleFileChange(doc.type, e.target.files?.[0] || null)}
                      className="block w-full text-sm text-slate-700 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                      disabled={uploading}
                    />
                  </div>
                  {files[doc.type] ? <FaCheckCircle className="text-green-500 h-5 w-5" /> : null}
                </div>
              ))}
            </div>
            <button
              className={`w-full py-3 rounded-xl font-bold text-lg transition-all duration-300 ${canUpload ? 'bg-blue-600 text-white hover:bg-blue-700' : 'bg-slate-200 text-slate-400 cursor-not-allowed'}`}
              onClick={handleUpload}
              disabled={!canUpload || uploading}
            >
              {'Upload Documents'}
            </button>
          </>
        )}
        {analyzing && (
          <div className="flex flex-col items-center justify-center min-h-[300px]">
            <svg className="animate-spin h-12 w-12 text-blue-600 mb-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8z"></path>
            </svg>
            <div className="text-xl font-bold text-slate-800 mb-2">Analyzing your documents...</div>
            <div className="text-slate-500 text-center">This may take up to 1 minute. Please wait.</div>
          </div>
        )}
        {success && (
          <div className="flex flex-col items-center justify-center min-h-[300px]">
            <FaCheckCircle className="text-green-500 h-16 w-16 mb-6" />
            <div className="text-2xl font-bold text-green-700 mb-2 text-center">Documents Approved!</div>
            <div className="text-slate-700 text-center mb-6">Your documents have been approved! You are now eligible to select a scholarship.</div>
            <button
              className="w-full py-3 rounded-xl font-bold text-lg bg-blue-600 text-white hover:bg-blue-700 transition-all duration-300"
              onClick={onSuccess}
            >
              Continue
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

