interface AuthContextType {
  user: User | null;
  supabaseUser: SupabaseUser | null;
  userProfile: UserProfile | null;
  login: (email: string, password: string) => Promise<void>;
  logout: () => Promise<void>;
  register: (email: string, password: string, userData: { name: string; role: 'student' | 'school'; [key: string]: any }) => Promise<void>;
  switchRole: (newRole: 'student' | 'school' | 'admin') => void;
  isAuthenticated: boolean;
  loading: boolean;
  updateUserProfile: (updates: Partial<UserProfile>) => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [supabaseUser, setSupabaseUser] = useState<SupabaseUser | null>(null);
  const [userProfile, setUserProfile] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const navigate = useNavigate();

  console.log('AUTH_DEBUG: AuthProvider renderizado.');
  console.log('AUTH_DEBUG: Estado inicial: loading=', loading, 'user=', user?.id);

  useEffect(() => {
    console.log('AUTH_DEBUG: useEffect disparado.');
    setLoading(true);

    const buildUser = (sessionUser: any, currentProfile: UserProfile | null): User => {
      console.log('AUTH_DEBUG: buildUser - Iniciando construÃ§Ã£o de objeto User. sessionUser ID:', sessionUser?.id, 'currentProfile ID:', currentProfile?.id);
      let role = sessionUser?.user_metadata?.role;
      if (!role && currentProfile) {
        if (currentProfile.is_admin) role = 'admin';
        else if (currentProfile.status === 'school') role = 'school';
        else role = 'student';
      }
      if (!role) {
        role = getDefaultRole(sessionUser?.email || '');
      }
      const builtUser: User = {
        id: sessionUser.id,
        email: sessionUser.email,
        name: sessionUser.user_metadata?.name || sessionUser.email?.split('@')[0] || '',
        role,
        university_id: currentProfile?.university_id ?? undefined,
        hasPaidProcess: currentProfile?.has_paid_selection_process_fee,
      };
      console.log('AUTH_DEBUG: buildUser - Objeto User construÃ­do:', builtUser);
      return builtUser;
    };

    const fetchAndSetUser = async (session: any) => {
      if (session?.user) {
        let profile: UserProfile | null = null;
        try {
          const { data, error } = await supabase
            .from('user_profiles')
            .select('*')
            .eq('user_id', session.user.id)
            .single();
          if (error && error.code !== 'PGRST116') { // PGRST116 = no rows found
            throw error;
          }
          profile = data;
        } catch (error) {
          console.error("Error fetching user profile:", error);
        }
        setUserProfile(profile);
        setUser(buildUser(session.user, profile));
        setSupabaseUser(session.user);
        console.log('AUTH_DEBUG: onAuthStateChange - User e UserProfile setados com sucesso.');
      } else {
        setUser(null);
        setSupabaseUser(null);
        setUserProfile(null);
        console.log('AUTH_DEBUG: onAuthStateChange - User e UserProfile resetados para null.');
      }
    };

    // Check for session on initial load
    supabase.auth.getSession().then(({ data: { session } }) => {
      fetchAndSetUser(session);
      setLoading(false);
    });

    // Listen for auth state changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      async (_event, session) => {
        fetchAndSetUser(session);
      }
    );

    return () => {
      console.log('AUTH_DEBUG: useEffect cleanup.');
      subscription.unsubscribe();
    };
  }, []);

  const updateUserProfile = async (updates: Partial<UserProfile>) => {
    if (!supabaseUser) {
      throw new Error("User must be logged in to update profile");
    }

    // Remove user_id from updates if it exists, as it should not be updated.
    const { user_id, ...updateData } = updates;

    const { data, error } = await supabase
      .from('user_profiles')
      .update(updateData)
      .eq('user_id', supabaseUser.id)
      .select()
      .single();

    if (error) {
      throw error;
    }

    if (data) {
      setUserProfile(data as UserProfile);
    }
  };

  const getDefaultRole = (email: string): 'student' | 'school' | 'admin' => {
    // Admin emails can be hardcoded or checked against a list
    const adminEmails = ['admin@matriculausa.com', 'admin@example.com'];
    if (adminEmails.includes(email.toLowerCase())) {
      return 'admin';
    }
    return 'student';
  };

  const switchRole = (newRole: 'student' | 'school' | 'admin') => {
    if (user) {
      const updatedUser = { ...user, role: newRole };
      setUser(updatedUser);

      // Update user metadata in Supabase
      supabase.auth.updateUser({
        data: { role: newRole }
      }).catch(error => {
        console.error('Error updating user role:', error);
      });
    }
  };

  const login = async (email: string, password: string) => {
    const { error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (error) {
      throw error;
    }
    // Redirection will be handled by the auth state change listener
  };

  const logout = async () => {
    setLoading(true);
    const { error } = await supabase.auth.signOut();
    if (error) {
      console.error('Error logging out:', error);
      // Opcional: notificar o usuÃ¡rio sobre o erro
    }
    // Limpar o estado local independentemente do erro do Supabase
    setUser(null);
    setUserProfile(null); 
    setLoading(false);
    console.log("AUTH_DEBUG: User logged out, user and userProfile set to null.");
  };

  const register = async (email: string, password: string, userData: { name: string; role: 'student' | 'school'; [key: string]: any }) => {
    const { error } = await supabase.auth.signUp({
      email,
      password,
      options: {
        data: userData,
      }
    });

    if (error) {
      throw error;
    }
    // Registration redirection will be handled in the Auth component
  };

  const value: AuthContextType = {
    user,
    supabaseUser,
    userProfile,
    login,
    logout,
    register,
    switchRole,
    isAuthenticated: !!user && !!supabaseUser,
    loading,
    updateUserProfile,
  };

  console.log('AUTH_DEBUG: AuthProvider renderizando com estados: user=', user?.id, 'userProfile=', userProfile, 'loading=', loading);

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

import { useEffect, useState } from 'react';
import { supabase } from '../lib/supabase';
import type { Scholarship } from '../types';

export function useScholarships() {
  const [scholarships, setScholarships] = useState<Scholarship[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchScholarships() {
      setLoading(true);
      const { data, error } = await supabase
        .from('scholarships')
        .select(`id, title, description, amount, deadline, requirements, field_of_study, level, eligibility, benefits, is_exclusive, is_active, university_id, created_at, updated_at, needcpt, visaassistance, scholarshipvalue, image_url, original_value_per_credit, original_annual_value, annual_value_with_scholarship, scholarship_type, universities!inner(id, name, logo_url, location, is_approved)`)
        .eq('is_active', true);
      if (error) {
        setError(error.message);
        setScholarships([]);
      } else {
        const filtered = (data || []).filter((s: any) => s.universities && s.universities.is_approved);
        setScholarships(filtered as unknown as Scholarship[]);
      }
      setLoading(false);
    }
    fetchScholarships();
  }, []);

  return { scholarships, loading, error };
}

import { useState, useEffect } from 'react';
import { supabase } from '../lib/supabase';

export function useScholarships() {
  const [scholarships, setScholarships] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchScholarships = async () => {
      setLoading(true);
      setError(null);
      try {
        const { data, error } = await supabase
          .from('scholarships')
          .select('*, university:university_id(*)')
          .eq('is_active', true);
        if (error) throw error;
        const approvedScholarships = (data || []).filter(s => s.university && s.university.is_approved);
        setScholarships(approvedScholarships);
      } catch (err: any) {
        setError('Failed to load scholarships');
        setScholarships([]);
      } finally {
        setLoading(false);
      }
    };
    fetchScholarships();
  }, []);

  return { scholarships, loading, error };
}

import { useState, useEffect } from 'react';
import { supabase } from '../lib/supabase';
import { useAuth } from './useAuth';

interface SubscriptionData {
  subscription_id: string | null;
  subscription_status: string;
  price_id: string | null;
  current_period_start: number | null;
  current_period_end: number | null;
  cancel_at_period_end: boolean;
  payment_method_brand: string | null;
  payment_method_last4: string | null;
}

interface OrderData {
  order_id: number;
  checkout_session_id: string;
  payment_intent_id: string;
  amount_total: number;
  currency: string;
  payment_status: string;
  order_status: string;
  order_date: string;
}

export function useSubscription() {
  const [subscription, setSubscription] = useState<SubscriptionData | null>(null);
  const [orders, setOrders] = useState<OrderData[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { user, isAuthenticated } = useAuth();

  useEffect(() => {
    if (!isAuthenticated || !user) {
      setSubscription(null);
      setOrders([]);
      setLoading(false);
      return;
    }

    async function fetchSubscriptionData() {
      setLoading(true);
      setError(null);

      try {
        // Fetch subscription data
        const { data: subscriptionData, error: subscriptionError } = await supabase
          .from('stripe_user_subscriptions')
          .select('*')
          .maybeSingle();

        if (subscriptionError) {
          console.error('Error fetching subscription:', subscriptionError);
          setError('Failed to load subscription data');
        } else {
          setSubscription(subscriptionData);
        }

        // Fetch orders data
        const { data: ordersData, error: ordersError } = await supabase
          .from('stripe_user_orders')
          .select('*')
          .order('order_date', { ascending: false });

        if (ordersError) {
          console.error('Error fetching orders:', ordersError);
          setError((prev) => prev ? `${prev}. Failed to load orders data` : 'Failed to load orders data');
        } else {
          setOrders(ordersData || []);
        }
      } catch (err: any) {
        console.error('Error in subscription hook:', err);
        setError(err.message || 'An unexpected error occurred');
      } finally {
        setLoading(false);
      }
    }

    fetchSubscriptionData();
  }, [user, isAuthenticated]);

  const hasActiveSubscription = subscription?.subscription_status === 'active';

  const hasPaidProcess = orders.some(order => 
    order.payment_status === 'paid' && order.order_status === 'completed'
  );

  return {
    subscription,
    orders,
    loading,
    error,
    hasActiveSubscription,
    hasPaidProcess
  };
}

