        .from('scholarship_applications')
        .select(`
          *,
          scholarships!inner (
            title,
            amount,
            universities!inner (
              name
            )
          )
        `)
        .order('created_at', { ascending: false });

      if (applicationsError) {
        console.error('Error loading applications:', applicationsError);
        // Don't throw here, continue with empty array
      }

      // Load admin logs
