        });

        if (error) throw error;

        // Update local state
        setUniversities(prev => prev.filter(u => u.id !== universityId));
        setStats(prev => ({
          ...prev,
          totalUniversities: prev.totalUniversities - 1,
          pendingUniversities: prev.pendingUniversities - 1
