    }
    if (!hasLowerCase) {
      return 'A senha deve conter pelo menos uma letra minÃºscula';
    }
    if (!hasNumbers) {
      return 'A senha deve conter pelo menos um nÃºmero';
    }
    if (!hasSpecialChar) {
