          )}

          {/* Step 4: Academic Information */}
          {currentStep === 4 && (
            <div className="space-y-6">
              <div className="text-center mb-6">
                <Users className="h-12 w-12 text-[#05294E] mx-auto mb-3" />
                <h2 className="text-xl font-bold text-gray-900">Academic Information</h2>
                <p className="text-gray-600">Tell us about your academic programs</p>
              </div>

              <div className="grid grid-cols-1 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Academic Programs *</label>
                  <div className="space-y-3">
                    <div className="flex gap-2">
                      <input
                        type="text"
                        value={newProgram}
                        onChange={(e) => setNewProgram(e.target.value)}
                        className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#05294E] focus:border-[#05294E]"
                        placeholder="Enter program name"
                        onKeyPress={(e) => e.key === 'Enter' && addProgram()}
                      />
                      <button
                        type="button"
                        onClick={addProgram}
                        className="bg-[#05294E] text-white px-4 py-2 rounded-lg hover:bg-[#05294E]/90 transition-colors"
                      >
                        <Plus className="h-4 w-4" />
                      </button>
                    </div>

                    {formData.programs.length > 0 && (
                      <div className="flex flex-wrap gap-2">
                        {formData.programs.map((program, index) => (
                          <span
                            key={index}
                            className="inline-flex items-center bg-gray-100 text-gray-700 px-3 py-1 rounded-full text-sm"
                          >
                            {program}
                            <button
                              type="button"
                              onClick={() => removeProgram(index)}
                              className="ml-2 text-gray-500 hover:text-red-600"
                            >
                              <X className="h-3 w-3" />
                            </button>
                          </span>
                        ))}
                      </div>
                    )}

                    {errors.programs && <p className="text-red-600 text-xs">{errors.programs}</p>}
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Navigation Buttons */}
          <div className="flex justify-between items-center mt-8 pt-6 border-t border-gray-200">
            <button
              type="button"
              onClick={prevStep}
              disabled={currentStep === 1}
              className={`px-6 py-2 rounded-lg font-medium transition-colors ${
                currentStep === 1
                  ? 'bg-gray-100 text-gray-400 cursor-not-allowed'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              Previous
            </button>

            <div className="text-sm text-gray-500">
              Step {currentStep} of 4
            </div>

            {currentStep < 4 ? (
              <button
                type="button"
                onClick={nextStep}
                className="bg-[#05294E] text-white px-6 py-2 rounded-lg font-medium hover:bg-[#05294E]/90 transition-colors"
              >
                Next
              </button>
            ) : (
              <button
                type="button"
                onClick={handleSubmit}
                disabled={loading}
                className="bg-[#05294E] text-white px-6 py-2 rounded-lg font-medium hover:bg-[#05294E]/90 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
              >
                {loading ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Saving...
                  </>
                ) : (
                  <>
                    <CheckCircle className="h-4 w-4 mr-2" />
                    Complete Setup
                  </>
                )}
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SchoolProfileSetup;

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  User, 
  Award, 
  BookOpen, 
  Settings, 
  Search, 
  Filter, 
  Calendar, 
  DollarSign, 
  CheckCircle, 
  Clock, 
  AlertCircle,
  Edit,
  FileText,
  Plus,
  ArrowRight,
  Sparkles,
  Target,
  Heart,
  Building,
  MapPin,
  Star,
  Zap
} from 'lucide-react';
import { supabase } from '../lib/supabase';
import type { Scholarship, Application, StudentProfile } from '../types';
import { useAuth } from '../hooks/useAuth';
import { useScholarships } from '../hooks/useScholarships';

const StudentDashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'overview' | 'scholarships' | 'applications' | 'profile'>('overview');
  const { scholarships: allScholarships, loading: scholarshipsLoading, error: scholarshipsError } = useScholarships();
  const [applications, setApplications] = useState<Application[]>([]);
  const [profile, setProfile] = useState<StudentProfile | null>(null);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedLevel, setSelectedLevel] = useState('all');
  const [showProfileEdit, setShowProfileEdit] = useState(false);
  const { user } = useAuth();

  const [profileForm, setProfileForm] = useState({
    name: '',
    phone: '',
    country: '',
    field_of_interest: '',
    academic_level: '',
    gpa: '',
    english_proficiency: ''
  });

  useEffect(() => {
    if (user) {
      loadDashboardData();
    }
  }, [user]);

  const loadDashboardData = async () => {
    if (!user) return;

    setLoading(true);
    try {

      const { data: profileData, error: profileError } = await supabase
        .from('user_profiles')
        .select(`
          id,
          full_name,
          phone,
          country,
          field_of_interest,
          academic_level,
          gpa,
          english_proficiency,
          created_at,
          updated_at
        `)
        .eq('user_id', user.id)
        .single();

      if(profileError && profileError.code !== 'PGRST116') throw profileError;

      const studentProfile: StudentProfile = {
        id: profileData?.id || user.id,
        name: profileData?.full_name || user.name || user.email?.split('@')[0] || '',
        email: user.email,
        phone: profileData?.phone,
        country: profileData?.country,
        field_of_interest: profileData?.field_of_interest,
        academic_level: profileData?.academic_level,
        gpa: profileData?.gpa,
        english_proficiency: profileData?.english_proficiency,
        created_at: profileData?.created_at || new Date().toISOString(),
        updated_at: profileData?.updated_at || new Date().toISOString()
      };

      setProfile(studentProfile);
      setProfileForm({
        name: studentProfile.name,
        phone: studentProfile.phone || '',
        country: studentProfile.country || '',
        field_of_interest: studentProfile.field_of_interest || '',
        academic_level: studentProfile.academic_level || '',
        gpa: studentProfile.gpa?.toString() || '',
        english_proficiency: studentProfile.english_proficiency || ''
      });

      const { data: applicationsData, error: applicationsError } = await supabase
        .from('scholarship_applications')
        .select(`
          *,
          scholarships (
            *
          )
        `)
        .eq('student_id', user.id);

      if (applicationsError) throw applicationsError;

      setApplications(applicationsData || []);

    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleApplyScholarship = async (scholarshipId: string) => {
    if (!user) return;

    // Check if already applied
    const alreadyApplied = applications.some(app => app.scholarship_id === scholarshipId);
    if (alreadyApplied) {
      alert('You have already applied for this scholarship');
      return;
    }

    try {
      const { data, error } = await supabase
        .from('scholarship_applications')
        .insert({
          scholarship_id: scholarshipId,
          student_id: user.id,
          status: 'pending',
        })
        .select( `
          *,
          scholarships (
            *
          )
        `)
        .single();

      if (error) throw error;

      setApplications(prev => [...prev, data as Application]);
      alert('Application submitted successfully!');
    } catch (error) {
      console.error('Error applying for scholarship:', error);
      alert('Error submitting application. Please try again.');
    }
  };

  const handleProfileUpdate = async () => {
    if (!user || !profile) return;

    try {
      const { data, error } = await supabase
        .from('user_profiles')
        .update({
          full_name: profileForm.name,
          phone: profileForm.phone,
          country: profileForm.country,
          field_of_interest: profileForm.field_of_interest,
          academic_level: profileForm.academic_level,
          gpa: parseFloat(profileForm.gpa) || null,
          english_proficiency: profileForm.english_proficiency,
          updated_at: new Date().toISOString()
        })
        .eq('user_id', user.id)
        .select()
        .single();

      if(error) throw error;

      setProfile(prev => ({...prev, ...data} as StudentProfile));
      setShowProfileEdit(false);
      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Error updating profile. Please try again.');
    }
  };

  const filteredScholarships = allScholarships.filter(scholarship => {
    const matchesSearch = (scholarship.title?.toLowerCase() || '').includes(searchTerm.toLowerCase()) ||
                         (scholarship.description?.toLowerCase() || '').includes(searchTerm.toLowerCase());
    const matchesLevel = selectedLevel === 'all' || (scholarship.level && scholarship.level === selectedLevel);
    return matchesSearch && matchesLevel;
  });

  const formatAmount = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0
    }).format(amount);
