import React, { useState, useEffect } from 'react';
import { useCartStore } from '../../stores/applicationStore';
import { useNavigate } from 'react-router-dom';
import { ShoppingCart, Trash2, CheckCircle, Loader2 } from 'lucide-react';
import { StripeCheckout } from '../../components/StripeCheckout';
import { useAuth } from '../../hooks/useAuth';
import { DocumentUpload } from '../../components/DocumentUpload';
import DocumentUploadModal from '../../components/DocumentUploadModal';

const STRIPE_CHECKOUT_URL = 'https://checkout.stripe.com/pay/cs_test_YOUR_CHECKOUT_LINK'; // Substitua pelo link real do produto Stripe
const SCHOLARSHIP_PRICE = 550;

const CartPage: React.FC = () => {
  const { cart, removeFromCart, clearCart, fetchCart, isLoading } = useCartStore();
  const navigate = useNavigate();
  const { user, userProfile, updateUserProfile } = useAuth();
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [selectedScholarship, setSelectedScholarship] = useState<string | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);

  useEffect(() => {
    if (user) {
      fetchCart(user.id);
    }
  }, [user, fetchCart]);

  const total = SCHOLARSHIP_PRICE;

  const handleNextStep = () => {
    setShowUploadModal(true);
  };

  const handleUploadSuccess = async () => {
    setIsProcessing(true);
    try {
      if (userProfile) {
        await updateUserProfile({ documents_status: 'approved' });
      }
      setShowUploadModal(false);
    } catch (error) {
      console.error('Failed to update user profile:', error);
      // Optionally, show an error message to the user
    } finally {
      setIsProcessing(false);
    }
  };

  const handleProceedToPayment = () => {
    if (selectedScholarship) {
      // Logic to proceed to payment with the selected scholarship
      console.log('Proceeding to payment for:', selectedScholarship);
      // For now, let's navigate to a generic checkout page
      // In a real scenario, you would pass scholarship details to the checkout
      navigate('/student/dashboard/checkout');
    }
  };

  const handleRemoveFromCart = (scholarshipId: string) => {
    if (user) {
      removeFromCart(scholarshipId, user.id);
    }
  };

  const handleClearCart = () => {
    if (user) {
      clearCart(user.id);
    }
  };

  const renderCartContents = () => {
    if (isLoading) {
      return (
        <div className="flex justify-center items-center h-48">
          <Loader2 className="animate-spin h-8 w-8 text-blue-600" />
        </div>
      );
    }

    if (userProfile?.documents_status === 'approved') {
      return (
        <div>
          <h2 className="text-xl font-bold mb-4 text-green-600 flex items-center gap-2">
            <CheckCircle /> Documents Approved! Please select one scholarship to proceed.
          </h2>
          <ul className="divide-y divide-slate-200 mb-8">
            {cart.map((scholarship) => (
              <li key={scholarship.id} className="flex items-center justify-between py-4">
                <label className="flex items-center gap-4 cursor-pointer w-full">
                  <input
                    type="radio"
                    name="scholarship"
                    value={scholarship.id}
                    checked={selectedScholarship === scholarship.id}
                    onChange={() => setSelectedScholarship(scholarship.id)}
                    className="h-5 w-5 text-blue-600 focus:ring-blue-500 border-gray-300 rounded-full"
                  />
                  <div>
                    <div className="font-bold text-slate-900">{scholarship.title}</div>
                    <div className="text-slate-600 text-sm">{scholarship.universities?.name || 'Unknown University'}</div>
                  </div>
                </label>
                <span className="font-bold text-green-700 text-lg">${SCHOLARSHIP_PRICE}</span>
              </li>
            ))}
          </ul>
          <div className="mt-6">
            <StripeCheckout
                productId="APPLICATION_FEE"
                paymentType="application_fee"
                feeType="application_fee"
                scholarshipsIds={selectedScholarship ? [selectedScholarship] : []}
                successUrl={`${window.location.origin}/application-fee/success?session_id={CHECKOUT_SESSION_ID}`}
                cancelUrl={`${window.location.origin}/application-fee/cancel`}
                buttonText="Proceed to Payment"
                className="w-full bg-green-600 hover:bg-green-700 disabled:bg-slate-300 disabled:cursor-not-allowed"
                disabled={!selectedScholarship || isProcessing}
                metadata={{ selected_scholarship_id: selectedScholarship }}
             />
          </div>
        </div>
      );
    }

    return (
      <div>
        <ul className="divide-y divide-slate-200 mb-8">
          {cart.map((scholarship) => (
            <li key={scholarship.id} className="flex items-center justify-between py-4">
              <div>
                <div className="font-bold text-slate-900">{scholarship.title}</div>
                <div className="text-slate-600 text-sm">{scholarship.universities?.name || 'Unknown University'}</div>
              </div>
              <div className="flex items-center gap-4">
                <span className="font-bold text-green-700 text-lg">${total}</span>
                <button
                  onClick={() => handleRemoveFromCart(scholarship.id)}
                  className="text-red-600 hover:text-red-800"
                >
                  <Trash2 className="h-5 w-5" />
                </button>
              </div>
            </li>
          ))}
        </ul>
        <div className="flex items-center justify-between mb-6">
          <span className="font-bold text-slate-700 text-lg">Total</span>
          <span className="font-bold text-blue-700 text-2xl">${total * cart.length}</span>
        </div>
        <button
          onClick={handleNextStep}
          disabled={isProcessing}
          className="w-full bg-blue-600 text-white py-3 rounded-xl font-bold hover:bg-blue-700 transition-all duration-300 mb-4 disabled:bg-slate-300"
        >
          {isProcessing ? 'Processing...' : 'Next Step'}
        </button>
        <button
          onClick={handleClearCart}
          className="w-full bg-slate-100 text-slate-700 py-3 px-6 rounded-2xl font-medium text-sm hover:bg-slate-200 transition-all duration-300"
        >
          Clear Cart
        </button>
      </div>
    );
  };

  return (
    <div className="max-w-2xl mx-auto py-12 px-4">
      <h1 className="text-3xl font-bold mb-8 flex items-center gap-2">
        <ShoppingCart className="h-7 w-7 text-[#05294E]" /> Cart
      </h1>
      {cart.length === 0 ? (
        <div className="bg-white rounded-2xl shadow-sm border border-slate-200 p-12 text-center">
          <h2 className="text-xl font-bold text-slate-700 mb-4">Your cart is empty</h2>
          <button
            onClick={() => navigate('/student/dashboard/scholarships')}
            className="bg-[#05294E] text-white px-6 py-3 rounded-xl font-bold hover:bg-[#05294E]/90 transition-all"
          >
            Browse Scholarships
          </button>
        </div>
      ) : (
        <div className="bg-white rounded-2xl shadow-sm border border-slate-200 p-8">
          {renderCartContents()}
        </div>
      )}
      {showUploadModal && userProfile && (
        <DocumentUploadModal
          userId={userProfile.user_id}
          onSuccess={handleUploadSuccess}
          onClose={() => setShowUploadModal(false)}
        />
      )}
    </div>
  );
};

export default CartPage;

import React, { useState } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { useCartStore } from '../../stores/applicationStore';
import { DocumentUpload } from '../../components/DocumentUpload';
import { useNavigate } from 'react-router-dom';

const DocumentsAndScholarshipChoice: React.FC = () => {
  const { userProfile } = useAuth();
  const { cart, clearCart } = useCartStore();
  const [documentsApproved, setDocumentsApproved] = useState(userProfile?.documents_status === 'approved');
  const [selectedScholarshipId, setSelectedScholarshipId] = useState<string | null>(null);
  const navigate = useNavigate();

  const handleContinue = () => {
    if (selectedScholarshipId) {
      window.localStorage.setItem('selectedScholarshipId', selectedScholarshipId);
      navigate('/student/dashboard/college-enrollment-checkout');
    }
  };

  if (!userProfile) return null;

  return (
    <div className="max-w-2xl mx-auto py-12 px-4">
      <h1 className="text-3xl font-extrabold mb-8 text-center text-slate-800">Required Documents & Scholarship Choice</h1>
      {!documentsApproved ? (
        <>
          <p className="text-slate-600 text-center mb-6 text-lg">To proceed, please upload all required documents below. All files must be clear and legible.</p>
          <DocumentUpload userId={userProfile.user_id} onComplete={() => setDocumentsApproved(true)} />
        </>
      ) : (
        <div className="bg-white rounded-2xl shadow-md border border-slate-200 p-8 animate-fade-in">
          <h2 className="text-2xl font-bold mb-6 text-slate-800 text-center">Select Your Scholarship</h2>
          <p className="text-slate-600 text-center mb-4">Choose one scholarship to continue your application process.</p>
          <ul className="mb-6 space-y-4">
            {cart.map((scholarship) => (
              <li key={scholarship.id} className={`flex items-center justify-between p-4 rounded-xl border transition-all duration-200 ${selectedScholarshipId === scholarship.id ? 'border-blue-600 bg-blue-50 shadow-lg' : 'border-slate-200 bg-slate-50'}`}>
                <div>
                  <div className="font-bold text-slate-900 text-lg">{scholarship.title}</div>
                  <div className="text-slate-600 text-sm">{scholarship.universities?.name || 'Unknown University'}</div>
                </div>
                <button
                  onClick={() => setSelectedScholarshipId(scholarship.id)}
                  className={`px-5 py-2 rounded-xl font-bold transition-all duration-200 ${selectedScholarshipId === scholarship.id ? 'bg-blue-600 text-white shadow' : 'bg-slate-200 text-slate-700 hover:bg-blue-100'}`}
                >
                  {selectedScholarshipId === scholarship.id ? 'Selected' : 'Select'}
                </button>
              </li>
            ))}
          </ul>
          <button
            onClick={handleContinue}
            disabled={!selectedScholarshipId}
            className="w-full bg-blue-600 text-white py-4 rounded-xl font-extrabold text-lg hover:bg-blue-700 transition-all duration-300 disabled:opacity-50 shadow-lg"
          >
            Continue to College Enrollment Payment
          </button>
        </div>
      )}
    </div>
  );
};

