// Extrair dados do webhook
const emailData = $input.first().json;

// Validar dados obrigatÃ³rios
if (!emailData.userId || !emailData.content || !emailData.from) {
  throw new Error('Dados obrigatÃ³rios ausentes no webhook');
}

// Extrair informaÃ§Ãµes do email
const emailInfo = {
  userId: emailData.userId,
  from: emailData.from,
  subject: emailData.subject || 'Sem assunto',
  content: emailData.content,
  timestamp: emailData.timestamp,
  messageId: emailData.messageId || `msg_${Date.now()}`,
  // Extrair domÃ­nio do remetente
  senderDomain: emailData.from.split('@')[1] || '',
  // Detectar se Ã© email de universidade
  isUniversityEmail: emailData.from.toLowerCase().includes('university') || 
                     emailData.from.toLowerCase().includes('edu') ||
                     emailData.from.toLowerCase().includes('college'),
  // Detectar urgÃªncia
  isUrgent: (emailData.subject || '').toLowerCase().includes('urgent') ||
            (emailData.subject || '').toLowerCase().includes('asap') ||
            (emailData.content || '').toLowerCase().includes('urgent'),
  // Detectar tipo de email
  emailType: detectEmailType(emailData.subject, emailData.content)
};

function detectEmailType(subject, content) {
  const subjectLower = (subject || '').toLowerCase();
  const contentLower = (content || '').toLowerCase();

  if (subjectLower.includes('application') || contentLower.includes('application')) {
    return 'application';
  }
  if (subjectLower.includes('document') || contentLower.includes('document')) {
    return 'document_request';
  }
  if (subjectLower.includes('payment') || contentLower.includes('payment')) {
    return 'payment';
  }
  if (subjectLower.includes('scholarship') || contentLower.includes('scholarship')) {
    return 'scholarship';
  }
  if (subjectLower.includes('admission') || contentLower.includes('admission')) {
    return 'admission';
  }
  return 'general';
}

return [{
  json: emailInfo
}];

// Preparar dados para anÃ¡lise de IA
const emailData = $input.first().json;
const userInfo = $('Get User Info').first().json;
const universityInfo = $('Get University Info').first().json;
const templateInfo = $('Get Response Template').first().json;

// Construir contexto para IA
const context = {
  emailType: emailData.emailType,
  isUrgent: emailData.isUrgent,
  isUniversityEmail: emailData.isUniversityEmail,
  userRole: userInfo?.role || 'student',
  userName: userInfo?.user_name || 'User',
  universityName: universityInfo?.name || 'University',
  originalEmail: {
    from: emailData.from,
    subject: emailData.subject,
    content: emailData.content,
    timestamp: emailData.timestamp
  },
  template: templateInfo?.ai_prompt || 'Respond professionally to this email',
  requiresHumanReview: templateInfo?.requires_human_review || false
};

// Determinar se deve usar IA ou template fixo
const shouldUseAI = !context.requiresHumanReview && 
                    (context.emailType !== 'general' || context.isUrgent);

return [{
  json: {
    ...context,
    shouldUseAI,
    templateId: templateInfo?.id,
    responseTemplate: templateInfo?.response_template
  }
}];

