// Processar resposta da IA
const aiResponse = $input.first().json;
const context = $('Prepare AI Context').first().json;

// Extrair resposta da IA
const aiContent = aiResponse.choices?.[0]?.message?.content || 'Thank you for your email. We will get back to you soon.';

// Preparar email de resposta
const responseEmail = {
  to: context.originalEmail.from,
  subject: `Re: ${context.originalEmail.subject}`,
  content: aiContent,
  isUrgent: context.isUrgent,
  emailType: context.emailType,
  templateId: context.templateId,
  generatedBy: 'ai'
};

// Se nÃ£o deve usar IA, usar template fixo
if (!context.shouldUseAI && context.responseTemplate) {
  responseEmail.content = context.responseTemplate
    .replace('{{userName}}', context.userName)
    .replace('{{universityName}}', context.universityName);
  responseEmail.generatedBy = 'template';
}

return [{
  json: responseEmail
}];

